import { Card,CardText, CardTitle,CardBody,Button } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';



const Cards = ({taskObj, index, deleteTask, updateListArray}) => {
	const [modal, setModal] = useState(false);

	const handleDelete = () =>{
		deleteTask(index);
	}

	const toggle = () => {
		setModal(!modal);
	}

	console.log(taskObj.Name)
	return (

			<Card style={{ "margin": "10px", width: '18rem'}}>
			<CardBody>	
				<CardTitle tag="h5"> {taskObj.Name}</CardTitle>
				<CardText>{taskObj.Description}</CardText>
				<Button color ="success" onClick = {() => setModal(true) }>Edit</Button>  

				<Button color= "danger" onClick  = {handleDelete}>Delete</Button>
			</CardBody>
			</Card> 


	);
};
export default Cards;
